import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button } from "reactstrap";


const CLI = props => {
  const [prefix, setPrefix] = useState('');
  const [availableCommands, setAvailableCommands] = useState([]);
  const [showSignature, setShowSignature] = useState(true);
  const [prevList, setPrevList] = useState([]);
  const [input, setInput] = useState('');
  const tree = ['portfolio', ['resume.txt', ['projects', ['project_1', ['project_2']]]]];
  const [currentDirectory, setCurrentDirectory] = useState(tree);
  const [currentDepth, setCurrentDepth] = useState(0);

  useEffect(() => {
    document.body.classList.toggle("index-page");
    setAvailableCommands([
      {
        name: 'clear',
        action: clearTerminal
      },
      {
        name: 'help',
        action: renderHelp
      },
      {
        name: 'cd',
        action: changeDirectory,
      },
      {
        name: 'ls',
        action: listDirectory,
      }
    ]);
  }, []);

  const setFocus = () => {
    document.getElementById("input").focus();
  }

  const renderSignature = () => {
    var ascii = `
 _______  ______    _______  __   __  _______  ______     _______  _______  _______  _______  __   __  ______    ___  
 |       ||    _ |  |   _   ||  |_|  ||       ||      |   |       ||       ||  _    ||  _    ||  | |  ||    _ |  |   | 
 |    _  ||   | ||  |  |_|  ||       ||   _   ||  _    |  |    ___||   _   || |_|   || |_|   ||  | |  ||   | ||  |   | 
 |   |_| ||   |_||_ |       ||       ||  | |  || | |   |  |   | __ |  | |  ||       ||       ||  |_|  ||   |_||_ |   | 
 |    ___||    __  ||       ||       ||  |_|  || |_|   |  |   ||  ||  |_|  ||  _   | |  _   | |       ||    __  ||   | 
 |   |    |   |  | ||   _   || ||_|| ||       ||       |  |   |_| ||       || |_|   || |_|   ||       ||   |  | ||   | 
 |___|    |___|  |_||__| |__||_|   |_||_______||______|   |_______||_______||_______||_______||_______||___|  |_||___| 
 `;
    return (
      <div>
        <pre style={{textAlign: 'center'}}>
          <code>
            {ascii}
          </code>
          <code>
            <br/>Type "help" to see the list of all available commands
          </code>
        </pre>
      </div>
    );
  }

  const executeCommand = (e) => {
    e.preventDefault();
    var nList = prevList.slice();
    nList.push({
      type: 'input',
      value: input
    });
    var found = false;
    for (var i=0; i < availableCommands.length; i++) {
      var cmd = availableCommands[i];
      if (cmd.name === input) {
        found = true;
        return cmd.action(nList);
      }
    }
    if(!found) {
      nList.push({
        type: 'error',
        value: 'command not found: ' + input
      });
    }
    setPrevList(nList);
    setInput('');
  }

  const listDirectory = nList => {
    const [parent, children] = currentDirectory;
    var list = '';
    children.map(child => {
      if(Array.isArray(child)) {
        console.log('child', child);
        list += child[0] + ' ';
      } else {
        list += child + ' ';
      }
    });
    nList.push({
      type: 'output',
      value: list
    });
    setPrevList(nList);
    setInput('');
  }

  const changeDirectory = (nList, input) => {
    const [parent, children] = currentDirectory;
    children.map((child, idx) => {
      if(Array.isArray(child)) {
        if (child[0] === input) {
          currentDirectory(child)
        }
      }
    })
  }

  const renderHelp = (nList) => {
    nList.push({
      type: 'output',
      value: "Allowed commands are 'ls', 'clear', 'help'"
    }); 
    setPrevList(nList);
    setInput('');
  }
  const clearTerminal = () => {
    setPrevList([]);
    setInput('');
  }

  const renderOutput = (cmd, idx) => {
    if (cmd) {
      if (cmd.type === 'error') {
        return (
          <div key={idx}>
            <pre>
              <code style={styles.error}>
                {cmd.value}
              </code>
            </pre>
          </div>
        )
      } else if (cmd.type === 'input') {
        return (
          <div style={{display: 'flex', flex: '1', flexDirection: 'row',alignItems: 'center'}} key={idx}>
            <p style={styles.terminalArrow}>{'>>'}</p>
            <p style={styles.prevInput}>{cmd.value}</p>
          </div>
        )
      } else if (cmd.type === 'output') {
        return (
          <code style={{color: 'white'}}>
            {cmd.value}
          </code>
        )
      }
    } else {
      return null;
    }
  }

  const renderPrevList = () => {
    if(prevList.length > 0) {
      return (
        <div>
          {prevList.map((cmd, idx) => {
            return renderOutput(cmd, idx);
          })}
        </div>
      )
    } else {
      return null;
    }
  }

  return (
    <div className="wrapper">
      <div className="page-header header-filter">
        <div className="squares square1" />
        <div className="squares square2" />
        <div className="squares square3" />
        <div className="squares square4" />
        <div className="squares square5" />
        <div className="squares square6" />
        <div className="squares square7" />
        <Container style={styles.container}>          
          <div style={styles.terminalWindow} onClick={setFocus}>
            {renderSignature()}
            {renderPrevList()}
            <div style={{display: 'flex', flex: '1', flexDirection: 'row',alignItems: 'center'}}>
              {prefix && (
                <p style={styles.terminalPrefix}>{prefix}</p>
              )}
              <p style={styles.terminalArrow}>{'>>'}</p>
              <form onSubmit={executeCommand}>
                <input id="input" autoFocus={true} autoComplete="off" autoCorrect={false} type="text" value={input} style={styles.terminalInput} onChange={e => setInput(e.target.value) }/>
              </form>
            </div>
            {renderOutput()}
          </div>
        </Container>
      </div>
    </div>
  )
};

const styles = {
  container: {
    alignItems: 'center',
    justifyContent: 'center',
    display: 'flex',
    overflow: 'hidden',
  },
  terminalWindow: {
    backgroundColor: 'rgba(0, 0, 0, 0.6)',
    width: '100%', 
    height: '90%', 
    borderRadius: 10, 
    borderColor: 'white', 
    borderWidth: 1,
    padding: 20,
    overflowY: 'scroll'
  },
  terminalPrefix: {
    fontSize: 20,
    color: '#39ff14',
    marginTop: 7,
    marginRight: 5,
    fontFamily: 'Courier, Monaco, monospace',
  },
  terminalArrow: {
    fontSize: 30,
    color: '#39ff14',
    paddingTop: 5,
    marginRight: 6,
    fontFamily: 'Courier, Monaco, monospace',
  },
  terminalInput: {
    color: 'white',
    backgroundColor: 'transparent',
    borderWidth: 0,
    outline: 'none',
    fontSize: 18,
    fontFamily: 'Courier, Monaco, monospace',
  },
  error: {
    color: 'red',
    fontSize: 16
  },
  prevInput: {
    color: 'white',
    backgroundColor: 'transparent',
    borderWidth: 0,
    outline: 'none',
    fontSize: 18,
    marginTop: 5,
    fontFamily: 'Courier, Monaco, monospace',
  }
}

export default CLI;
