import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button } from "reactstrap";

import CursorImitator from '../components/CursorImitator';

const CLI = props => {
  const [availableCommands, setAvailableCommands] = useState([]);
  const [showSignature, setShowSignature] = useState(true);
  const [input, setInput] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    document.body.classList.toggle("index-page");
    setAvailableCommands([
      'help',
      'ls',
      'open',
    ]);
  }, []);

  const setFocus = () => {
    document.getElementById("input").focus();
  }

  const renderSignature = () => {
    var ascii = `
 _______  ______    _______  __   __  _______  ______     _______  _______  _______  _______  __   __  ______    ___  
 |       ||    _ |  |   _   ||  |_|  ||       ||      |   |       ||       ||  _    ||  _    ||  | |  ||    _ |  |   | 
 |    _  ||   | ||  |  |_|  ||       ||   _   ||  _    |  |    ___||   _   || |_|   || |_|   ||  | |  ||   | ||  |   | 
 |   |_| ||   |_||_ |       ||       ||  | |  || | |   |  |   | __ |  | |  ||       ||       ||  |_|  ||   |_||_ |   | 
 |    ___||    __  ||       ||       ||  |_|  || |_|   |  |   ||  ||  |_|  ||  _   | |  _   | |       ||    __  ||   | 
 |   |    |   |  | ||   _   || ||_|| ||       ||       |  |   |_| ||       || |_|   || |_|   ||       ||   |  | ||   | 
 |___|    |___|  |_||__| |__||_|   |_||_______||______|   |_______||_______||_______||_______||_______||___|  |_||___| 
 `;
    return (
      <div>
        <pre style={{textAlign: 'center'}}>
          <code>
            {ascii}
          </code>
          <code>
            <br/>Type "help" to see the list of all available commands
          </code>
        </pre>
      </div>
    );
  }

  const renderError = () => {

  }

  const executeCommand = (e) => {
    e.preventDefault();
    console.log('command executed');
    if(availableCommands.includes(input)) {

    } else {

    }
  }

  return (
    <div className="wrapper">
      <div className="page-header header-filter">
        <div className="squares square1" />
        <div className="squares square2" />
        <div className="squares square3" />
        <div className="squares square4" />
        <div className="squares square5" />
        <div className="squares square6" />
        <div className="squares square7" />
        <Container style={styles.container}>          
          <div style={styles.terminalWindow} onClick={setFocus}>
            {renderSignature()}
            <div style={{display: 'flex', flex: '1', flexDirection: 'row',alignItems: 'center'}}>
              <p style={styles.terminalArrow}>{'>>'}</p>
              <form onSubmit={executeCommand}>
                <input id="input" autoFocus={true} autoComplete="off" autoCorrect={false} type="text" value={input} style={styles.terminalInput} onChange={e => setInput(e.target.value) }/>
              </form>
            </div>
          </div>
        </Container>
      </div>
    </div>
  )
};

const styles = {
  container: {
    alignItems: 'center',
    justifyContent: 'center',
    display: 'flex'
  },
  terminalWindow: {
    backgroundColor: 'rgba(0, 0, 0, 0.6)',
    width: '100%', 
    height: '90%', 
    borderRadius: 10, 
    borderColor: 'white', 
    borderWidth: 1,
    padding: 20,
  },
  terminalArrow: {
    fontSize: 30,
    color: '#39ff14',
    paddingTop: 5,
    marginRight: 6,
  },
  terminalInput: {
    color: 'white',
    backgroundColor: 'transparent',
    borderWidth: 0,
    outline: 'none',
    fontSize: 18
  }
}

export default CLI;
