import React, { useEffect } from "react";
import {
  Container,
  Row,
  Col,
} from "reactstrap";
import IndexNavbar from "components/Navbars/IndexNavbar.js";

import { 
  RenderIcon,
  ReactNativeIcon,
  DjangoIcon,
  JSIcon
} from '../components/Icons';
import ProjectsLinksButton from '../components/ProjectLinksButton';
import data from '../data.json';

const Projects = props => {
  const projects = data.projects;
  useEffect(() => {
    document.documentElement.scrollTop = 0;
    document.body.classList.toggle("profile-page");
    return () => {
      document.body.classList.toggle("profile-page");
    }
  }, []);

  const displayBackgroundIndex = num => {
    if (num < 10) {
      return '0' + num;
    }
    return '' + num;
  }

  return (
    <div>
      <IndexNavbar />
      <div className="wrapper">
        {
          projects.map((project, index) => {
            if (index === 0) {
              return (
                <div className="page-header">
                  <img
                    alt="..."
                    className="dots"
                    src={require("assets/img/dots.png")}
                  />
                  <img
                    alt="..."
                    className="path"
                    src={require("assets/img/path4.png")}
                  />
                  <Container className="section">
                    <h2>My Projects</h2>
                    <Row>
                      <Col lg="5" md="5">
                        <h1 className="profile-title text-left">{project.name}</h1>
                        <h5 className="text-on-back">{displayBackgroundIndex(index + 1)}</h5>
                        <p className="profile-description text-left">
                          {project.description}
                        </p>
                        <div className="btn-wrapper profile pt-3">
                          {
                            project.stacks.map(stack => (
                              <RenderIcon name={stack} />
                            ))
                          }
                          <ProjectsLinksButton
                            style={{marginLeft: 10}}
                            links={project.links}
                          />
                        </div>
                      </Col>
                      <Col md="6">
                        <Row className="justify-content-between">
                          <img style={{width: '100%', height: 'auto', marginLeft: 30}} src="https://www.pramodgobburi.com/assets/invoice_manager-37e2d2e54779a844b4098679aa80cd7e3a543de6f92cfbb1122cd54424c6487b.png" />
                        </Row>
                      </Col>
                    </Row>         
                  </Container>
                </div>
              );
            }
          })
        }

        <Container className="section">
          <Row className="justify-content-between">
            <Col md="6">
              <Row className="justify-content-between">
                <img style={{width: '100%', height: 'auto', marginLeft: 30}} src="https://www.pramodgobburi.com/assets/invoice_manager-37e2d2e54779a844b4098679aa80cd7e3a543de6f92cfbb1122cd54424c6487b.png" />
              </Row>
            </Col>
            <Col md="5">
              <h1 className="profile-title text-left">Personal Portfolio (v2)</h1>
              <h5 className="text-on-back">02</h5>
              <p className="profile-description text-left">
                My Current personal website designed in React, complete with project
                portfolio, and a fun command line interface.
              </p>
              <div className="btn-wrapper profile pt-3">
                <ReactNativeIcon />
                <JSIcon />
              </div>
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
};

export default Projects;
