import React, { useState } from "react";
import classnames from "classnames";
import { Container, Card, CardHeader, Nav, NavItem, NavLink, CardBody, TabContent, TabPane } from "reactstrap";
import data from "../data.json";

const Experience = props => {
  const [currentTabIndex, setCurrentTabIndex] = useState(0);
  const experiences = data.experience;

  return (
    <div>
      {/* <img alt="..." className="path" src={require("assets/img/path5.png")} /> */}
      <Container>
        <h2 className="title">Work Experience</h2>
        <Card>
          <CardHeader style={{alignItems: 'center'}}>
            <Nav className="nav-tabs-info" role="tablist" tabs>
              {
                experiences.map((experience, index) => (
                  <NavItem>
                    <NavLink
                      className={classnames({
                        active: currentTabIndex === index
                      })}
                      onClick={e => setCurrentTabIndex(index)}
                      style={{cursor: 'pointer'}}
                    >
                      Gophr App Inc
                    </NavLink>
                  </NavItem>
                ))
              }
              {/* <NavItem>
                <NavLink
                  className={classnames({
                    active: currentTabIndex === 0
                  })}
                  onClick={e => setCurrentTabIndex(0)}
                  style={{cursor: 'pointer'}}
                >
                  Gophr App Inc
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink
                  className={classnames({
                    active: currentTabIndex === 1
                  })}
                  onClick={e => setCurrentTabIndex(1)}
                  style={{cursor: 'pointer'}}
                >
                  Freelance
                </NavLink>
              </NavItem> */}
            </Nav>
          </CardHeader>
          <CardBody>
            <TabContent
              className="tab-space"
              activeTab={"link" + currentTabIndex}
            >
              <TabPane tabId="link0">
                <Container>
                  <div style={{textAlign: 'center'}}>
                    <h6>June 2019 - Present</h6>
                    <img
                      alt="..."
                      style={{width: 55, height: 50, marginTop: 10, marginBottom: 10}}
                      src={require("assets/img/gophr_logo.png")}
                    />
                    <h3 style={{marginBottom: 10}}>Gophr App Inc</h3>
                    <h4>Full-Stack Software Engineer</h4>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'center', borderBottomColor: 'white', borderBottomWidth: 5}}>
                    <ul style={{maxWidth: 700}}>
                      <li style={{marginBottom: 3}}>Led multiple development teams, coordinated, and developed multiple projects.</li>
                      <li style={{marginBottom: 3}}>Developed a mobile marketplace application using React Native.</li>
                      <li style={{marginBottom: 3}}>Developed responsive dashboards using React.</li>
                      <li style={{marginBottom: 3}}>Designed and implemented cloud infrastructure in AWS.</li>
                      <li>Designed and developed full-stack applications using Django and MeteorJS and multiple microservices using ExpressJS.</li>
                    </ul>
                  </div>
                </Container>
              </TabPane>
              <TabPane tabId="link1">
                <Container>
                  <div style={{textAlign: 'center'}}>
                    <h6>Nov 2017 - June 2019</h6>
                    <img
                      alt="..."
                      style={{width: 55, height: 50, marginTop: 10, marginBottom: 10}}
                      src={require("assets/img/gophr_logo.png")}
                    />
                    <h3 style={{marginBottom: 10}}>Freelance Development</h3>
                    <h4>Software Developer</h4>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'center', borderBottomColor: 'white', borderBottomWidth: 5}}>
                    <ul style={{maxWidth: 700}}>
                      <li style={{marginBottom: 3}}>Offered consulting services for local businesses regarding software systems design.</li>
                      <li style={{marginBottom: 3}}>Designed, developed and published Android applications.</li>
                      <li style={{marginBottom: 3}}>Built various software components for a self-order kiosk and a mobile app.</li>
                      <li style={{marginBottom: 3}}>Developed REST APIs and full stack web applications using Django framework.</li>
                      <li>Developed responsive web applications and dashboards using Angular.</li>
                    </ul>
                  </div>
                </Container>
              </TabPane>
            </TabContent>
          </CardBody>
        </Card>
      </Container>
    </div>
  );
}

export default Experience;
