import React from "react";
import CursorImitator from '../CursorImitator';
import { CSSTransition } from 'react-transition-group';

// reactstrap components
import { Container, Row, Col, Button } from "reactstrap";

class PageHeader extends React.Component {
  render() {
    return (
      <div className="page-header" style={{height: '70vh', minHeight: 0, maxHeight: '570vh'}}>
        {/* <div className="squares square1" />
        <div className="squares square2" />
        <div className="squares square3" />
        <div className="squares square4" />
        <div className="squares square5" />
        <div className="squares square6" />
        <div className="squares square7" /> */}
        <Container>
          <div className="content-center brand">

            <h1 className="h1-seo">
              <CursorImitator 
                content={['Pramod Gobburi']}
              />
            </h1>
            <div className="button-container">
              <h2>
                Software Engineer
              </h2>
              <Row>
                <Col md="12">
                  <Button className="btn-simple" color="primary">GitHub</Button>
                  <Button className="btn-simple" color="success">LinkedIn</Button>
                  <Button className="btn-simple btn-neutral" color="default">
                    PDF Resume
                  </Button>
                </Col>
              </Row>
              <h2 className="title">About Me</h2>
      <h4>I am a Computer Science graduate from McNeese State University, software developer at Gophr App LLC. I am experienced and knowledgeable in technical problem solving, programming, and am always driven to learn new things.</h4>
            </div>

          </div>
        </Container>
      </div>
    );
  }
}

export default PageHeader;
